// Helper functions

// formatCell converts a cell value to a display string.
func formatCell(v interface{}) string {
	if v == nil {
		return "-"
	}

	switch val := v.(type) {
	case pgtype.Numeric:
		if !val.Valid {
			return "-"
		}
		f, err := val.Float64Value()
		if err != nil || !f.Valid {
			return "-"
		}
		return formatNumber(f.Float64)

	case pgtype.Date:
		if !val.Valid {
			return "-"
		}
		return val.Time.Format("2006-01-02")

	case pgtype.Text:
		if !val.Valid {
			return "-"
		}
		return val.String

	case pgtype.Bool:
		if !val.Valid {
			return "-"
		}
		if val.Bool {
			return "Yes"
		}
		return "No"

	case time.Time:
		if val.IsZero() {
			return "-"
		}
		return val.Format("2006-01-02")

	case bool:
		if val {
			return "Yes"
		}
		return "No"

	case string:
		if val == "" {
			return "-"
		}
		return val

	default:
		return fmt.Sprintf("%v", v)
	}
}

