// getColumnMeta returns the ColumnMeta for a given column name.
func getColumnMeta(col string, meta []ColumnMeta) *ColumnMeta {
	for i := range meta {
		if meta[i].Name == col {
			return &meta[i]
		}
	}
	return nil
}

